pragma solidity 0.8.17;

contract People {

    struct Person {
        string name;
        uint age;
    }

    Person public moi;
    Person[] public persons;

    function modifyPerson(string memory _name, uint _age) public {
        moi.name = _name;
        moi.age = _age;
