g","name":"race","type":"string"},{"internalType":"uint256","name":"taille","type":"uint256"},{"internalType":"uint256","name":"age","type":"uint256"},{"internalType":"bool","name":"isAdopted","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"id","type":"uint256"}],"name":"getAnimal","outputs":[{"components":[{"internalType":"string","name":"race","type":"string"},{"internalType":"uint256","name":"taille","type":"uint256"},{"internalType":"uint256","name":"age","type":"uint256"},{"internalType":"bool","name":"isAdopted","type":"bool"}],"internalType":"struct SPA.Animal","name":"","type":"tuple"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"","type":"address"}],"name":"masters","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"animalId","type":"uint256"}],"name":"removeAnimal","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"id","type":"uint256"},{"internalType":"string","name":"_race","type":"string"},{"internalType":"uint256","name":"_taille","type":"uint256"},{"internalType":"uint256","name":"_age","type":"uint256"}],"name":"setAnimal","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/exercices/SPA.sol":"SPA"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"contracts/exercices/SPA.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

contract SPA {

    struct Animal {
        string race;
        uint taille;
        uint age;
        bool isAdopted;
    }

    Animal[] public animals;

    mapping(address => uint) public masters;

    event NewAnimalAvailable(uint id, string race, uint taille, uint age);

    event AnimalDied(uint id);

    event AnimalAdopted(uint id);

    function addAnimal(string calldata _race, uint _taille, uint _age) external {
        animals.push(Animal(_race, _taille, _age, false));
        emit NewAnimalAvailable(animals.length - 1, _race, _taille, _age);
    }

    function removeAnimal(uint animalId) external {
        delete animals[animalId];
        emit AnimalDied(animalId);
    }

    function getAnimal(uint id) external view returns (Animal memory) {
        return animals[id];
    }

    function setAnimal(uint id, string calldata _race, uint _taille, uint _age) external {
        animals[id].race = _race;
        animals[id].taille = _taille;
        animals[id].age = _age;
    }

    function adoptByCriteria(string calldata _race, uint _taille, uint _age) external {

        for (uint i = 0; i < animals.length; i++) {
            if (animals[i].isAdopted == false && keccak256(abi.encodePacked(animals[i].race)) == keccak256(abi.encodePacked(_race)) && animals[i].taille < _taille && animals[i].age < _age) {
                masters[msg.sender] = i;
                emit AnimalAdopted(i);
                break;
            }
        }
        revert('No animal matches with your criterias');
    }

    function adoptById(uint id) external {
        require(animals[id].isAdopted == false, 'Animal already adopted');
        masters[msg.sender] = id;
        emit AnimalAdopted(id);
    }

    function adoptedBy(address master) external view returns (Animal memory) {
        return animals[masters[master]];
    }


}
","keccak256":"0xe59900ba3fe794b16467f8cbb16924f3b29713a95c2192c90034eecd863349f2","license":"GPL-3.0"}},"version":1}

