pragma solidity 0.8.17;

contract Contacts {
  uint _count = 0; // state variable

  struct Contact {
    uint id;
    string name;
    string phone;
  }

  event ContactAdded(uint id);

  mapping(uint => Contact) public contacts;

  function count() public view returns (uint) {
    return _count;
  }

  function createContact(string memory _name, string memory _phone) public {
    _count++;
    contacts[_count] = Contact(_count, _name, _phone);
    emit ContactAdded(_count);
  }
}","keccak256":"0x77fe1b88cbbf1e4595130635fe65c199d433109e64fc7e7be635780e52421100","license":"GPL-3.0"}},"version":1}
